#include <iostream>#include <string>#include <vector>#include <ctime>#include <fstream>#include <filesystem>using namespace std;#define LOG(severity) Logger::getInstance(INFO)enum Level {	INFO,};class Logger {	static Logger infoLogger;public:	static Logger& getInstance(Level severity) {		if (severity == INFO)			return infoLogger;	}	Logger& operator<<(int value) {		printf("%d", value);		return *this;	}};Logger Logger::infoLogger;int main() {	Logger::getInstance(INFO) << 42;	LOG(INFO) << 42;	cout << INFO;}
